// import SideNavigationBar from "./sideNavigationBar";
// import DisplayArea from "./displayArea";
// import OperationArea from "./operationArea";
import "./index.css"
// import React, { useState } from 'react';
// function Console() {
//     return (
//         <div id="father">
//             <SideNavigationBar />
//             <DisplayArea />
//             <OperationArea />
//         </div>
//     );
// }
// export default Console;
import React, { useState } from 'react';

function LeftChild() {
    return (
        <div id="leftChild">
            <div id="title">
                <span>布局排版</span>
            </div>
            <div id="componentBox"></div>
        </div>
    );
}

function MidumChild() {
    const [startX, setStartX] = useState(0);
    const [startWidth, setStartWidth] = useState(0);
    const [startWidth1, setStartWidth1] = useState(0);
    const [num, setNum] = useState(1);

    const toggleSidebar = () => {
        const div = document.getElementById('rightChild');
        const div1 = document.getElementById('midueChild');
        const div3 = document.getElementById('leftChild');

        if (num === 1) {
            div1.style.maxWidth = '80%';
            div.style.maxWidth = '60%';
            div1.style.width = '80%';
            div3.style.minWidth = '0px';
            setNum(0);
        } else {
            div1.style.maxWidth = '60%';
            div.style.maxWidth = '40%';
            div1.style.width = '60%';
            div3.style.minWidth = '20%';
            setNum(1);
        }

        div.classList.toggle('collapsed');
        div3.classList.toggle('collapsed');
        document.getElementById('button').classList.toggle('rotated');
    };

    return (
        <div id="midueChild">
            <button id="button" onClick={toggleSidebar}>
                《-
            </button>
            <div id="html"></div>
        </div>
    );
}

function RightChild() {
    const [startX, setStartX] = useState(0);
    const [startWidth, setStartWidth] = useState(0);
    const [startWidth1, setStartWidth1] = useState(0);
    const [isMouseMove, setIsMouseMove] = useState(false);

    const handleMouseDown = (event) => {
        const div = document.getElementById('rightChild');
        const div1 = document.getElementById('midueChild');
        const rect = div.getBoundingClientRect();
        const leftBorder = rect.left;
        const mouseX = event.clientX;

        if (mouseX >= leftBorder - 5 && mouseX <= leftBorder + 5) {
            setStartX(event.clientX);
            setStartWidth(parseInt(window.getComputedStyle(div).width, 10));
            setStartWidth1(parseInt(window.getComputedStyle(div1).width, 10));
            setIsMouseMove(true);
        }
    };

    const handleMouseMove = (event) => {
        if (isMouseMove) {
            const div = document.getElementById('rightChild');
            const div1 = document.getElementById('midueChild');
            const width = event.clientX - startX + startWidth;
            div.style.width = width + 'px';
            div1.style.width = startWidth1 - width + 'px';
        }
    };


    const handleMouseUp = () => {
        if (isMouseMove) {
            setIsMouseMove(false);
        }
    };

    return (
        <div
            id="rightChild"
            onMouseDown={handleMouseDown}
            onMouseMove={handleMouseMove}
            onMouseUp={handleMouseUp}
        ></div>
    );
}

function Layout() {
    return (
        <div id="father">
            <LeftChild />
            <MidumChild />
            <RightChild />
        </div>
    );
}

export default Layout;
