import sideNavigationBar from "./sideNavigationBar";
import displayArea from "./displayArea";
import operationArea from "./operationArea";
import "./index.css"
function Console() {

    // 获取需要改变宽度的div元素

    var div = document.getElementById('rightChild');
    var div1 = document.getElementById('midueChild');
    var startX, startWidth, startWidth1;

    // 鼠标按下时记录初始位置和宽度
    var mouseDownPromise = new Promise(function (resolve) {
        div.addEventListener('mousedown', function (event) {
            var rect = div.getBoundingClientRect();
            var leftBorder = rect.left;
            var mouseX = event.clientX;

            if (mouseX >= leftBorder - 5 && mouseX <= leftBorder + 5) {
                startX = event.clientX;
                startWidth = parseInt(document.defaultView.getComputedStyle(div).width, 10);
                startWidth1 = parseInt(document.defaultView.getComputedStyle(div1).width, 10);

                // 添加判断条件
                var isMouseMove = false;

                // 鼠标移动事件监听器
                function mouseMoveListener(event) {
                    isMouseMove = true;
                    var width = event.clientX - startX;
                    div.style.width = startWidth - width + 'px';
                    div1.style.width = startWidth1 + width + 'px';
                }

                // 鼠标松开事件监听器
                function mouseUpListener() {
                    if (isMouseMove) {
                        resolve();
                    }
                    document.removeEventListener('mousemove', mouseMoveListener);
                    document.removeEventListener('mouseup', mouseUpListener);
                }

                document.addEventListener('mousemove', mouseMoveListener);
                document.addEventListener('mouseup', mouseUpListener);
            }
        });
    });


    // 处理鼠标移动事件
    function mouseMoveHandler(event) {
        var width = event.clientX - startX;
        div.style.width = startWidth - width + 'px';
        div1.style.width = startWidth1 + width + 'px';
    }


    // 处理鼠标松开事件
    function mouseUpHandler() {
        document.removeEventListener('mousemove', mouseMoveHandler);
        document.removeEventListener('mouseup', mouseUpHandler);
    }

    // 使用.then()连接事件监听器
    mouseDownPromise.then(function () {
        document.addEventListener('mousemove', mouseMoveHandler);
        document.addEventListener('mouseup', mouseUpHandler);
    });


    var div3 = document.getElementById("leftChild");
    var num = 1;
    function toggleSidebar() {
        if (num == 1) {
            div1.style.maxWidth = '80%';
            div.style.maxWidth = '60%';
            div1.style.width = '80%';
            div3.style.minWidth = '0px'
            num = 0;
        }
        else {
            div1.style.maxWidth = '60%';
            div.style.maxWidth = '40%';
            div1.style.width = '60%';
            div3.style.minWidth = '20%';
            num = 1;
        }

        div.classList.toggle("collapsed");
        div3.classList.toggle("collapsed");
        document.getElementById("button").classList.toggle("rotated")
    }


    return (
        <div id="father">
            <sideNavigationBar></sideNavigationBar>
            <displayArea></displayArea>
            <operationArea></operationArea>
        </div>
    );
}
export default Console;
