import React, { useState } from 'react';

function OperationArea() {
    const [startX, setStartX] = useState(0);
    const [startWidth, setStartWidth] = useState(0);
    const [startWidth1, setStartWidth1] = useState(0);
    const [isMouseMove, setIsMouseMove] = useState(false);

    const handleMouseDown = (event) => {
        const rect = event.target.getBoundingClientRect();
        const leftBorder = rect.left;
        const mouseX = event.clientX;

        if (mouseX >= leftBorder - 5 && mouseX <= leftBorder + 5) {
            setStartX(event.clientX);
            setStartWidth(parseInt(window.getComputedStyle(event.target).width, 10));
            setStartWidth1(parseInt(window.getComputedStyle(document.getElementById('midueChild')).width, 10));
            setIsMouseMove(true);
        }
    };

    const handleMouseMove = (event) => {
        if (isMouseMove) {
            const div = document.getElementById('rightChild');
            const div1 = document.getElementById('midueChild');
            const width = event.clientX - startX;
            div.style.width = startWidth - width + 'px';
            div1.style.width = startWidth1 + width + 'px';
        }
    };

    const handleMouseUp = () => {
        if (isMouseMove) {
            setIsMouseMove(false);
        }
    };

    return (
        <div
            id="rightChild"
            onMouseDown={handleMouseDown}
            onMouseMove={handleMouseMove}
            onMouseUp={handleMouseUp}
        ></div>
    );
}

export default OperationArea;
